import React from "react";
import { createReaction } from "../actions";
import styles from "./ReactionIcon.module.css";
import { useReactionStore } from "../stores/reactions";
import { useUserStore } from "../stores/users";

const  ReactionIcon = ({ messageId,emoji }) => {
  const activeUserId = useUserStore((state) => state.activeUserId);
  const allReactions = useReactionStore((state) => state.reactions);
  const reactionsForMessage = allReactions.filter((reaction) => reaction.messageId === messageId && reaction.emoji === emoji);
  console.log(reactionsForMessage);
  const reactionFromUser = allReactions.filter((reaction) => reaction.messageId === messageId && reaction.emoji === emoji && reaction.userId===activeUserId);
  const count=reactionForMessage.length;
  return (
    <button className={styles.emojiButton} onClick={() =>createReaction(messageId,emoji)}>
        <span aria-label="emoji" role="img">
            {emoji} {count}
        </span>
    </button>
  );
};

export default ReactionIcon;
